        int[] b = new int[n + 1];
        st = new StringTokenizer(br.readLine());
        for (int i = 1; i <= n; i++) {
            a[i] = Integer.parseInt(st.nextToken());
            b[i] = Integer.parseInt(st.nextToken());
        }
        long ans = solve(n, k, a, b);
        System.out.println(ans == -1 ? -1 : (ans + k - 1) / k * k);
    }

    static long solve(int n, int k, int[] a, int[] b) {
        long[] f = new long[n + 1];
        long[] g = new long[n + 1];
        for (int i = 1; i <= n; i++) {
            if (b[i] == 1) {
                f[i] = f[i - 1] + a[i];
                g[i] = g[i - 1] + a[i];
            } else {
                f[i] = Math.max(f[i - 1], g[i - 1] + a[i]);
                g[i] = Math.max(g[i - 1], f[i - 1] + a[i]);
            }
        }
        long ans = -1;
        for (int i = 1; i <= n; i++) {
            if (b[i] == 1) {
                long t = f[n] - f[i] + g[i] - g[0];
                if (t >= k) {
                    ans = Math.max(ans, t);
                }
            } else {
                long t = f[n] - f[i] + g[n] - g[i];
                if (t >= k) {
                    ans = Math.max(ans, t);
                }
                t = f[n] - g[i] + g[0] - f[i];
                if (t >= k) {
                    ans = Math.max(ans, t);
                }
            }
        }
        return ans;
    }
