    for (int i = 1; i <= n; i++) {
        f[i] = (f[i - 1] * 2) % MOD;
    }
    vector<int> g(n + 1);
    g[0] = 1;
    for (int i = 1; i <= n; i++) {
        g[i] = (g[i - 1] * (power(2, a[i - 1] - a[i]) + 1)) % MOD;
    }
    long long ans = 0;
    for (int i = 0; i <= n; i++) {
        ans = (ans + f[i] * g[n - i]) % MOD;
    }
    cout << ans << endl;
