        for i in range(n):
            for j in range(n):
                if grid[i][j] == '.':
                    return False
        return True

    def get_color(i, j):
        if i == 0:
            return grid[i][j]
        return grid[i][j] if grid[i][j] == grid[i-1][j] else '.'

    def get_row(i):
        row = []
        for j in range(n):
            row.append(get_color(i, j))
        return row

    def get_col(j):
        col = []
        for i in range(n):
            col.append(get_color(i, j))
        return col

    def is_valid(i, j):
        return 0 <= i < n and 0 <= j < n

    def is_valid_row(i, row):
        for j in range(n):
            if not is_valid(i, j) or grid[i][j] != row[j]:
                return False
        return True

    def is_valid_col(j, col):
        for i in range(n):
            if not is_valid(i, j) or grid[i][j] != col[i]:
                return False
        return True

    def get_diff(i, j):
        row = get_row(i)
        col = get_col(j)
        diff = 0
        for k in range(n):
            if row[k] != col[k]:
                diff += 1
        return diff

    if is_possible():
        print(0)
        return

    dp = [[-1] * n for _ in range(n)]
    for i in range(n):
        for j in range(n):
            if grid[i][j] == '.':
                dp[i][j] = 0
            else:
                dp[i][j] = 1

