#include <stdio.h>
#include <string.h>

#define N 500005
#define M 100005

char s[N];
int n, q, l[M], r[M], ans[M];
int len[N], sum[N], tag[N];

void modify(int p, int v) {
    for (int i = p; i <= n; i += i & -i) {
        tag[i] += v;
    }
}

int query(int p) {
    int res = 0;
    for (int i = p; i >= 1; i -= i & -i) {
        res += tag[i];
    }
    return res;
}

int main() {
    scanf("%s", s + 1);
    n = strlen(s + 1);
    for (int i = 1; i <= n; i++) {
        len[i] = 1;
        if (i > 1 && s[i] == s[i - 1]) {
            len[i] = len[i - 1] + 1;
        }
    }
    for (int i = 1; i <= n; i++) {
        sum[i] = sum[i - 1] + len[i];
    }
    scanf("%d", &q);
    for (int i = 1; i <= q; i++) {
        scanf("%d %d", &l[i], &r[i]);
        ans[i] = 1;
    }
    for (int i = 1; i <= n; i++) {
        if (s[i] == 'z') {
            modify(i, 1);
        }
    }
    for (int i = 1; i <= q; i++) {
        int x = query(r[i]);
        int y = query(r[i] - 1);
        if (l[i] > 1) {
            x -= query(l[i] - 1);
            y -= query(l[i] - 2);
        }
        if (x - y >= (r[i] - l[i] + 1) / 2) {
            ans[i] = 0;
        }
    }
    for (int i = 1; i <= q; i++) {
        if (ans[i]) {
            printf("Yes\n");
        } else {
            printf("No\n");
        }
    }
    return 0;
