int ans[N];

void solve() {
    cin >> s;
    n = s.size();
    for (int i = 0; i < n; i++) {
        len[i] = s[i] - 'a';
    }
    for (int i = 1; i < n; i++) {
        len[i] = max(len[i], len[i - 1] + 1);
    }
    for (int i = 0; i < q; i++) {
        cin >> l[i] >> r[i];
        l[i]--, r[i]--;
        ans[i] = len[r[i]] - len[l[i] - 1];
    }
    for (int i = 0; i < q; i++) {
        cout << (ans[i] == r[i] - l[i] + 1 ? "No" : "Yes") << '\n';
    }
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);
    int T = 1;
    while (T--) {
        solve();
    }
    return 0;
}
