```python
import sys

class Node:
    def __init__(self):
        self.left = None
        self.right = None
        self.min = 0
        self.max = 0
        self.tag = 0

class SegmentTree:
    def __init__(self, n):
        self.n = n
        self.root = Node()

    def update(self, node, l, r, x, val):
        if l == r:
            node.min = node.max = val
            return
        mid = (l + r) // 2
        if x <= mid:
            self.update(node.left, l, mid, x, val)
        else:
            self.update(node.right, mid + 1, r, x, val)
        node.min = min(node.left.min, node.right.min)
        node.max = max(node.left.max, node.right.max)

    def query(self, node, l, r, x, y):
        if x <= l and r <= y:
            return node.min, node.max
        mid = (l + r) // 2
        if y < l or x > r:
            return float('inf'), float('-inf')
        res1, res2 = self.query(node.left, l, mid, x, y)
        res3, res4 = self.query(node.right, mid + 1, r, x, y)
        return min(res1, res3), max(res2, res4)

def solve():
    s = input()
    n = len(s)
    seg = SegmentTree(n)
    for i in range(n):
        if s[i] == 'z':
            seg.update(seg.root, 0, n - 1, i, 0)
        else:
            seg.update(seg.root, 0, n - 1, i, ord(s[i]) - ord('a') + 1)
    for _ in range(int(input())):
        l, r = map(int, input().split())
        l -= 1
        r -= 1
        if l == r:
            print('Yes')
            continue
        min_val, max_val = seg.query(seg.root, 0, n - 1, l, r)
        if max_val - min_val + 1 <= r - l:
            print('Yes')
        else:
            print('No')

solve()
