#define MOD 1000000007
#define N 1005

int n;
int a[N], b[N];
int fa[N], sz[N];
int dp[N][N];

void dfs(int u, int f) {
    fa[u] = f;
    sz[u] = 1;
    for (int i = 0; i < n; i++) {
        if (a[i] == u) {
            dfs(b[i], u);
            sz[u] += sz[b[i]];
        }
    }
}

int main() {
    scanf("%d", &n);
    for (int i = 0; i < n; i++) {
        scanf("%d%d", &a[i], &b[i]);
        a[i]--;
        b[i]--;
    }
    dfs(0, -1);
    for (int i = 0; i < n; i++) {
        dp[a[i]][b[i]] = 1;
        dp[b[i]][a[i]] = 1;
    }
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (fa[i] != j) {
                for (int k = 0; k < n; k++) {
                    if (fa[i] != k && fa[j] != k) {
                        dp[i][k] = (dp[i][k] + dp[j][k]) % MOD;
                        dp[k][i] = (dp[k][i] + dp[k][j]) % MOD;
                    }
                }
            }
        }
    }
    int ans = 0;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (fa[i] == -1 && fa[j] == -1) {
                ans = (ans + dp[i][j]) % MOD;
            }
        }
    }
    printf("%d\n", ans);
    return 0;
}
