#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

const int MOD = 1e9 + 7;

int dfs(int u, int p, vector<vector<int>>& adj, vector<int>& tin, vector<int>& tout, vector<int>& up, vector<int>& dep, vector<int>& par) {
    tin[u] = ++dep[u];
    up[u][0] = p;
    for (int i = 1; i < 20; i++) {
        up[u][i] = up[up[u][i - 1]][i - 1];
    }
    for (int v : adj[u]) {
        if (v == p) continue;
        par[v] = u;
        dep[v] = dep[u] + 1;
        dfs(v, u, adj, tin, tout, up, dep, par);
    }
    tout[u] = dep[u];
    return 0;
}

int lca(int u, int v, vector<int>& up, vector<int>& dep, vector<int>& par) {
    if (dep[u] < dep[v]) swap(u, v);
    for (int i = 19; i >= 0; i--) {
        if (dep[up[u][i]] >= dep[v]) u = up[u][i];
    }
    if (u == v) return u;
    for (int i = 19; i >= 0; i--) {
        if (up[u][i] != up[v][i]) {
            u = up[u][i];
            v = up[v][i];
        }
    }
    return par[v];
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n;
    cin >> n;
    vector<vector<int>> adj(n);
    for (int i = 0; i < n - 1; i++) {
        int u, v;
        cin >> u >> v;
        u--; v--;
        adj[u].push_back(v);
        adj[v].push_back(u);
    }

    vector<int> tin(n), tout(n), up(n, vector<int>(20)), dep(n), par(n);
    dfs(0, -1, adj, tin, tout, up, dep, par);

    vector<vector<int>> dp(n, vector<int>(n));
    for (int i = 0; i < n; i++) {
        dp[i][i] = 1;
    }
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            for (int k = 0; k < n; k++) {
                if (tin[i] <= tin[k] && tin[k] <= tout[i]) {
                    dp[i][j] = (dp[i][j] + dp[i][k] * dp[k][j]) % MOD;
                }
            }
        }
    }

    vector<int> ans(n);
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (i == j) continue;
            int lca_node = lca(i, j, up, dep, par);
            if (lca_node == i) {
                ans[i] = (ans[i] + dp[i][j]) % MOD;
            } else {
                ans[j] = (ans[j] + dp[i][j]) % MOD;
            }
        }
    }

    int res = 0;
    for (int i = 0; i < n; i++) {
        res = (res + ans[i]) % MOD;
    }
    cout << res << '\n';

    return 0;
}
