import java.io.*;
import java.util.*;

public class Main {
    static final int N = 1005;
    static final int mod = (int) 1e9 + 7;
    static int n, cnt;
    static int[] fa = new int[N];
    static int[] siz = new int[N];
    static int[] dep = new int[N];
    static int[] son = new int[N];
    static int[] top = new int[N];
    static int[] dfn = new int[N];
    static int[] col = new int[N];
    static int[] head = new int[N];
    static int[] to = new int[N << 1];
    static int[] nxt = new int[N << 1];
    static int tot;
    static int[] f = new int[N];
    static int[] g = new int[N];
    static int[] h = new int[N];
    static int[] ans = new int[N];

    static void add(int u, int v) {
        to[++tot] = v;
        nxt[tot] = head[u];
        head[u] = tot;
    }

    static void dfs1(int u, int fa) {
        siz[u] = 1;
        for (int i = head[u]; i != 0; i = nxt[i]) {
            int v = to[i];
            if (v == fa) continue;
            dep[v] = dep[u] + 1;
            dfs1(v, u);
            siz[u] += siz[v];
            if (siz[v] > siz[son[u]]) son[u] = v;
        }
    }

    static void dfs2(int u, int topf) {
        dfn[u] = ++cnt;
        top[u] = topf;
        if (son[u] != 0) dfs2(son[u], topf);
        for (int i = head[u]; i != 0; i = nxt[i]) {
            int v = to[i];
            if (v == fa[u] || v == son[u]) continue;
            dfs2(v, v);
        }
    }

    static void dfs3(int u, int fa) {
        for (int i = head[u]; i != 0; i = nxt[i]) {
            int v = to[i];
            if (v == fa) continue;
            dfs3(v, u);
            f[u] = (int) ((1L * f[u] * f[v] % mod + 1L * g[u] * h[v] % mod) % mod);
            g[u] = (int) ((1L * g[u] * f[v] % mod + 1L * h[u] * h[v] % mod) % mod);
            h[u] = (int) ((1L * h[u] * f[v] % mod + 1L * h[v] * f[u] % mod) % mod);
        }
    }

    static void dfs4(int u, int fa) {
        for (int i = head[u]; i != 0; i = nxt[i]) {
            int v = to[i];
            if (v == fa) continue;
            ans[v] = (int) ((1L * ans[u] * f[v] % mod + 1L * ans[u] * h[v] % mod) % mod);
            dfs4(v, u);
        }
    }

    public static void main(String[] args) throws IOException {
        Scanner sc = new Scanner(System.in);
        n = sc.nextInt();
        for (int i = 1; i < n; i++) {
            int u = sc.nextInt();
            int v = sc.nextInt();
            add(u, v);
            add(v, u);
        }
        dfs1(1, 0);
        dfs2(1, 1);
        for (int i = 1; i <= n; i++) {
            f[i] = g[i] = h[i] = 1;
        }
        dfs3(1, 0);
        for (int i = 1; i <= n; i++) {
            ans[i] = 1;
        }
        dfs4(1, 0);
        System.out.println(ans[1]);
    }
}

