#include <stdio.h>

const long long MOD = 1e9 + 7;

long long power(long long n) {
    long long result = 1;
    long long factorial = 1;
    for (long long i = 1; i <= n; i++) {
        result = (result * i) % MOD;
        factorial = (factorial * i) % MOD;
    }
    return (result * pow(factorial, MOD - 2, MOD)) % MOD;
}

int main() {
    long long n;
    scanf("%lld", &n);
    printf("%lld\n", power(n));
    return 0;
}

long long pow(long long a, long long b, long long mod) {
    long long result = 1;
    while (b > 0) {
        if (b & 1) {
            result = (result * a) % mod;
        }
        a = (a * a) % mod;
        b >>= 1;
    }
    return result;
}
