#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 100005

typedef struct {
    int val;
    int cnt;
} heap;

heap h[MAXN];
int n, a[MAXN], cnt[MAXN];

void swap(int i, int j) {
    heap temp = h[i];
    h[i] = h[j];
    h[j] = temp;
}

void heapify(int i) {
    int l = 2 * i + 1;
    int r = 2 * i + 2;
    int smallest = i;

    if (l < n && h[l].val < h[smallest].val) {
        smallest = l;
    }

    if (r < n && h[r].val < h[smallest].val) {
        smallest = r;
    }

    if (smallest != i) {
        swap(i, smallest);
        heapify(smallest);
    }
}

void build_heap() {
    for (int i = n / 2 - 1; i >= 0; i--) {
        heapify(i);
    }
}

void heap_push(int val) {
    h[n].val = val;
    h[n].cnt = 1;
    int i = n;
    n++;

    while (i > 0) {
        int parent = (i - 1) / 2;
        if (h[parent].val <= h[i].val) {
            break;
        }
        swap(i, parent);
        i = parent;
    }
}

int heap_pop() {
    if (n == 0) {
        return -1;
    }

    int ret = h[0].val;
    h[0] = h[n - 1];
    n--;
    heapify(0);
    return ret;
}

int main() {
    scanf("%d", &n);
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
        cnt[i] = 0;
    }

    int sum = 0;
    for (int i = 0; i < n; i++) {
        sum += a[i];
    }

    for (int i = 0; i < n; i++) {
        heap_push(i + 1);
    }

    int cur = 0;
    while (n > 0) {
        int max_val = heap_pop();
        int max_cnt = 0;
        while (n > 0 && h[n - 1].val == max_val) {
            max_cnt++;
            n--;
        }

        for (int i = 0; i < max_cnt; i++) {
            if (cur < sum) {
                cnt[max_val]++;
                cur++;
                heap_push(max_val);
            } else {
                break;
            }
        }
    }

    for (int i = 1; i <= n; i++) {
        printf("%d\n", cnt[i]);
    }

    return 0;
}
