        A.append(a)
        B.append(b)
        C.append(c)

    def check(x, y):
        return (A[x] * Mb + B[x] * Ma) % (Ma * Mb) == (A[y] * Mb + B[y] * Ma) % (Ma * Mb)

    def dfs(x, y, z):
        if x == N:
            if y == N:
                return C[z]
            else:
                return float('inf')
        if y == N:
            return float('inf')
        if check(x, y):
            return min(dfs(x + 1, y, z + 1), dfs(x, y + 1, z + 1))
        else:
            return dfs(x + 1, y, z)

    gcd, x, y = extended_gcd(Ma, Mb)
    Ma //= gcd
    Mb //= gcd
    if dfs(0, 0, 0) == float('inf'):
