public class Main {
    static int N, Mx, My;
    static int[][] A = new int[50][3];
    static int[] dp = new int[1 << 20];

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String[] line = br.readLine().split(" ");
        N = Integer.parseInt(line[0]);
        Mx = Integer.parseInt(line[1]);
        My = Integer.parseInt(line[2]);

        for (int i = 0; i < N; i++) {
            line = br.readLine().split(" ");
            A[i][0] = Integer.parseInt(line[0]);
            A[i][1] = Integer.parseInt(line[1]);
            A[i][2] = Integer.parseInt(line[2]);
        }

        Arrays.fill(dp, Integer.MAX_VALUE / 2);
        dp[0] = 0;

        for (int i = 0; i < (1 << N); i++) {
            for (int j = 0; j < N; j++) {
                if (((i >> j) & 1) == 1) {
                    for (int k = 0; k < N; k++) {
                        if (((i >> k) & 1) == 0) {
                            int next = i | (1 << k);
                            int cost = dp[i] + A[k][2];
                            int x = A[k][0] * Mx + A[k][1] * My;
                            int y = A[j][0] * Mx + A[j][1] * My;
                            if (x == y) {
                                dp[next] = Math.min(dp[next], cost);
                            }
                        }
                    }
                }
            }
        }

        int ans = dp[(1 << N) - 1];
        if (ans == Integer.MAX_VALUE / 2) {
            System.out.println(-1);
        } else {
            System.out.println(ans);
        }
    }
}
