#include <stdio.h>
#include <stdlib.h>

#define N 100005

typedef struct {
    int to, next;
} Edge;

typedef struct {
    int head[N], tot;
    Edge e[N << 1];
} Graph;

void add(Graph *g, int u, int v) {
    g->e[++g->tot] = (Edge) {v, g->head[u]};
    g->head[u] = g->tot;
}

int low[N], dfn[N], cnt, scc[N], scc_cnt, st[N], top;
bool inst[N];

void dfs1(Graph *g, int u, int fa) {
    dfn[u] = low[u] = ++cnt;
    st[++top] = u;
    for (int i = g->head[u]; i; i = g->e[i].next) {
        int v = g->e[i].to;
        if (!dfn[v]) {
            dfs1(g, v, u);
            low[u] = min(low[u], low[v]);
        } else if (!inst[v]) {
            low[u] = min(low[u], dfn[v]);
        }
    }
    if (dfn[u] == low[u]) {
        scc_cnt++;
        while (1) {
            int v = st[top--];
            inst[v] = 1;
            scc[v] = scc_cnt;
            if (u == v) break;
        }
    }
}

int a[N], deg[N];

void dfs2(Graph *g, int u, int fa) {
    for (int i = g->head[u]; i; i = g->e[i].next) {
        int v = g->e[i].to;
        if (v != fa) {
            a[scc[v]] += a[u];
            if (deg[v] == 1) {
                if (a[scc[v]]) {
                    printf("NO\n");
                    exit(0);
                }
            }
            dfs2(g, v, u);
        }
    }
}

int main() {
    int n;
    scanf("%d", &n);
    Graph g;
    g.tot = 0;
    for (int i = 1; i <= n; i++) {
        scanf("%d", &a[i]);
        deg[i] = 0;
    }
    for (int i = 1; i < n; i++) {
        int u, v;
        scanf("%d%d", &u, &v);
        add(&g, u, v);
        add(&g, v, u);
        deg[u]++;
        deg[v]++;
    }
    for (int i = 1; i <= n; i++) {
        if (!dfn[i]) dfs1(&g, i, 0);
    }
    for (int i = 1; i <= n; i++) {
        if (!inst[i]) dfs2(&g, i, 0);
    }
    printf("YES\n");
    return 0;
}
