import java.io.*;
import java.util.*;

public class Main {
    static int N;
    static int[] A;
    static List<Integer>[] G;
    static long[] dp;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st;

        N = Integer.parseInt(br.readLine());
        A = new int[N];
        st = new StringTokenizer(br.readLine());
        for (int i = 0; i < N; i++) {
            A[i] = Integer.parseInt(st.nextToken());
        }
        G = new List[N];
        for (int i = 0; i < N; i++) {
            G[i] = new ArrayList<>();
        }
        for (int i = 0; i < N - 1; i++) {
            st = new StringTokenizer(br.readLine());
            int u = Integer.parseInt(st.nextToken()) - 1;
            int v = Integer.parseInt(st.nextToken()) - 1;
            G[u].add(v);
            G[v].add(u);
        }
        dp = new long[N];
        dfs(0, -1);
        long sum = 0;
        for (int i = 0; i < N; i++) {
            sum += A[i];
        }
        pw.println(sum == dp[0] ? "YES" : "NO");
        pw.close();
    }

    static void dfs(int u, int p) {
        if (G[u].size() == 1 && G[u].get(0) == p) {
            dp[u] = A[u];
            return;
        }
        for (int v : G[u]) {
            if (v == p) continue;
            dfs(v, u);
        }
        long sum = 0;
        for (int v : G[u]) {
            if (v == p) continue;
            sum += dp[v];
        }
        dp[u] = Math.min(sum + A[u], A[u]);
    }
}
