int n, a[N], d[N], f[N], g[N], vis[N];
int e[N][N], cnt;

void dfs(int u, int fa) {
    f[u] = 1;
    for (int v = 0; v < n; v++) {
        if (e[u][v] && v != fa) {
            d[v] = d[u] + 1;
            dfs(v, u);
            f[u] += f[v];
        }
    }
}

void dfs2(int u, int fa) {
    g[u] = f[u];
    for (int v = 0; v < n; v++) {
        if (e[u][v] && v != fa) {
            g[u] -= f[v];
            dfs2(v, u);
            g[u] += f[v];
        }
    }
}

int main() {
