        scanf("%d%d", &u, &v);
        e[u][v] = e[v][u] = 1;
    }
    dfs(1, 0);
    dfs2(1, 0);
    for (int i = 1; i <= n; i++) {
        if (a[i] % 2 == 0 && g[i] > 0) {
            vis[i] = 1;
        }
    }
    for (int i = 1; i <= n; i++) {
