            dfs(v, u, g, a)
            a[u] += a[v]

def solve():
    n = int(input())
    a = [0] + list(map(int, input().split()))
    g = [[] for _ in range(n + 1)]
    for _ in range(n - 1):
        u, v = map(int, input().split())
        g[u].append(v)
        g[v].append(u)

    q = deque()
    for i in range(1, n + 1):
        if len(g[i]) == 1:
            q.append(i)

    while q:
        u = q.popleft()
        for v in g[u]:
            if a[u] % 2 == 0 and a[v] % 2 == 0:
                a[v] += a[u]
                g[v].append(u)
                q.append(v)
            elif a[u] % 2 == 1 and a[v] % 2 == 1:
                a[v] += a[u]
                g[v].append(u)
                q.append(v)

    dfs(1, 0, g, a)

    ans = []
    for i in range(1, n + 1):
        if a[i] % 2 == 1:
            ans.append(i)

    print(*sorted(ans))

