        for (int i = 1; i <= N; i++) {
            A[i] = Integer.parseInt(st.nextToken());
        }

        G = new List[N + 1];
        for (int i = 1; i <= N; i++) {
            G[i] = new ArrayList<>();
        }

        st = new StringTokenizer(br.readLine());
        for (int i = 1; i < N; i++) {
            int u = Integer.parseInt(st.nextToken());
            int v = Integer.parseInt(st.nextToken());
            G[u].add(v);
            G[v].add(u);
        }

        sum = new int[N + 1];
        vis = new boolean[N + 1];
        ans = new boolean[N + 1];

        dfs(1, 0);

        List<Integer> res = new ArrayList<>();
        for (int i = 1; i <= N; i++) {
            if (ans[i]) res.add(i);
        }

        Collections.sort(res);
        for (int x : res) pw.print(x + " ");
        pw.println();

        pw.close();
    }

    static void dfs(int u, int fa) {
        vis[u] = true;
        sum[u] = A[u];
        for (int v : G[u]) {
            if (v == fa) continue;
            dfs(v, u);
            sum[u] += sum[v];
        }
        if (sum[u] % 2 == 1) ans[u] = true;
        for (int v : G[u]) {
            if (v == fa) continue;
            if (sum[v] % 2 == 0) ans[u] = true;
        }
    }
}
