#include <stdio.h>
#include <stdlib.h>

#define MOD 1000000007

typedef long long ll;

ll power(ll a, ll b) {
    ll res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

int main() {
    int n, k;
    scanf("%d %d", &n, &k);
    ll fac[n + 1], inv[n + 1];
    fac[0] = 1;
    for (int i = 1; i <= n; i++) fac[i] = fac[i - 1] * i % MOD;
    inv[n] = power(fac[n], MOD - 2);
    for (int i = n - 1; i >= 0; i--) inv[i] = inv[i + 1] * (i + 1) % MOD;
    ll ans = 0;
    for (int i = 0; i <= k - 2; i++) {
        ll c = fac[k - 2 + i] * inv[i] % MOD * inv[k - 2 - i] % MOD;
        ll d = fac[n - k + 1 + i] * inv[i] % MOD * inv[n - k + 1 - i] % MOD;
        c = c * fac[n - k + 1] % MOD;
        d = d * fac[n - k + 1] % MOD;
        ans = (ans + c * d) % MOD;
    }
    ans = (ans + fac[n - k + 1] * inv[n - k + 1] % MOD) % MOD;
    printf("%lld\n", (ans + MOD) % MOD);
    return 0;
}
