        long[] inv = new long[MAXN];
        fac[0] = 1;
        for (int i = 1; i <= n; i++) {
            fac[i] = fac[i - 1] * i % MOD;
        }
        inv[n] = pow(fac[n], MOD - 2);
        for (int i = n - 1; i >= 0; i--) {
            inv[i] = inv[i + 1] * (i + 1) % MOD;
        }

        long ans = 0;
        for (int i = 0; i <= k - 1; i++) {
            long tmp = fac[k - 1 + i] * inv[i] % MOD * inv[k - 1 - i] % MOD;
            tmp = tmp * fac[n - k + i] % MOD * inv[n - k + i] % MOD;
            tmp = tmp * fac[n - k - 1] % MOD;
            ans = (ans + tmp) % MOD;
        }
        pw.println(ans);
        pw.close();
    }

    static long pow(long a, long b) {
        long res = 1;
        while (b > 0) {
            if ((b & 1) == 1) res = res * a % MOD;
            a = a * a % MOD;
            b >>= 1;
        }
        return res;
    }
