    scanf("%d", &n);

    Pair* pairs = (Pair*)malloc(n * sizeof(Pair));
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &pairs[i].a, &pairs[i].b);
    }

    int ans = 0;
    for (int i = 0; i < n; i++) {
        int g = gcd(pairs[i].a, pairs[i].b);
        int x = pairs[i].a / g;
        int y = pairs[i].b / g;
        int z = x - 1;
        ans += (z / y + 1) * y;
    }

    printf("%d\n", ans);
    free(pairs);
    return 0;
}
