    factors.erase(unique(factors.begin(), factors.end()), factors.end());

    vector<int> ans;
    for (int f : factors) {
        if ((N + M - 1) % (f - 1) == 0) {
            int n = N, m = M;
            vector<int> divisors;
            for (int i = 1; i * i <= f; i++) {
                if (f % i == 0) {
                    divisors.push_back(i);
                    if (i != f / i) {
                        divisors.push_back(f / i);
                    }
                }
            }
            sort(divisors.begin(), divisors.end());
            divisors.erase(unique(divisors.begin(), divisors.end()), divisors.end());
            int g = 0;
            for (int d : divisors) {
                if (d > 1 && (f - 1) % d == 0) {
                    g = gcd(g, d);
                }
            }
            int x = 0;
            for (int i = 0; i < (f - 1) / g; i++) {
                x += (n * m % MOD * i % MOD + n + m) % MOD;
                x %= MOD;
                n = (n * m % MOD + n + m) % MOD;
                m = (n + m) % MOD;
            }
            x = (x % MOD + MOD) % MOD;
            ans.push_back(x);
        }
