        }
        inv[n + m] = qpow(fac[n + m], MOD - 2);
        for (int i = n + m - 1; i >= 1; i--) {
            inv[i] = inv[i + 1] * (i + 1) % MOD;
        }

        long ans = 0;
        for (int i = 1; i <= n + m; i++) {
            if ((n + m - 1) % (k - 1) == 0 && (i - 1) % (k - 1) == 0) {
