long long count_divisors(int n) {
    long long res = 1;
    long long fact = 1;
    for (int i = 1; i <= n; i++) {
        fact = (fact * i) % MOD;
        long long count = 0;
        for (int j = 1; j * j <= i; j++) {
            if (i % j == 0) {
                if (i / j == j) {
                    count++;
                } else {
                    count += 2;
                }
            }
        }
        res = (res * factorial(count)) % MOD;
    }
    return res;
}

