                is_prime[j] = False
            primes.append(i)
    return primes

def compute_factorial_divisors(n, primes):
    factor = [0] * (n + 1)
    for i in range(1, n + 1):
        for p in primes:
            if p > i:
                break
            if i % p == 0:
                factor[i] += 1
                j = p
                while j <= i:
                    factor[i] += factor[j - 1]
                    j += p
    return factor

def main():
    n = int(input())
    primes = precompute()
    factor = compute_factorial_divisors(n, primes)
    ans = 1
    for i in range(1, n + 1):
        ans = (ans * (factor[i] + 1)) % MOD
    print(ans)

