#define MOD 1000000007

typedef long long ll;

ll dp[1001][1001][1001];
ll f[1001][1001];
ll inv[1001];

ll power(ll a, ll b) {
    ll res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

ll C(ll n, ll m) {
    if (n < m) return 0;
    return f[n][m] = (f[n - 1][m - 1] + f[n - 1][m]) % MOD;
}

int main() {
    int n, a, b, c, d;
    scanf("%d %d %d %d %d", &n, &a, &b, &c, &d);
    inv[1] = 1;
    for (int i = 2; i <= n; i++) inv[i] = (MOD - MOD / i) * inv[MOD % i] % MOD;
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= i; j++) {
            f[i][j] = C(i - 1, j - 1);
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = a; j <= b; j++) {
            for (int k = c; k <= d; k++) {
                if (j == i) {
                    dp[i][j][k] = 1;
                } else {
                    for (int l = 1; l <= i; l++) {
                        if (l >= c && l <= d) {
                            dp[i][j][k] = (dp[i][j][k] + dp[i - l][j - 1][l] * C(i - 1, l - 1) % MOD * inv[l] % MOD * inv[i - l]) % MOD;
                        }
                    }
                }
            }
        }
    }
    ll ans = 0;
    for (int i = a; i <= b; i++) {
        for (int j = c; j <= d; j++) {
