#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

const int MOD = 1e9 + 7;

long long power(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

long long inv(long long a) {
    return power(a, MOD - 2);
}

long long C(int n, int m) {
    if (n < m) return 0;
    long long res = 1;
    for (int i = 1; i <= m; i++) {
        res = res * (n - i + 1) % MOD * inv(i) % MOD;
    }
    return res;
}

long long f(int n, int a, int b, int c, int d, vector<vector<long long>>& dp) {
    if (n < 0) return 0;
    if (n == 0) return 1;
    if (dp[n][a] != -1) return dp[n][a];
    long long res = 0;
    for (int i = a; i <= b && i <= n; i++) {
        int k = n - i;
        int l = c;
        int r = min(d, k);
        for (int j = l; j <= r; j++) {
            res = (res + f(k, i, b, j, d, dp) * C(b, i) % MOD * C(n - 1, k)) % MOD;
        }
    }
    return dp[n][a] = res;
}

int main() {
    int n, a, b, c, d;
    cin >> n >> a >> b >> c >> d;
    vector<vector<long long>> dp(n + 1, vector<long long>(b + 1, -1));
    cout << f(n, a, b, c, d, dp) << endl;
    return 0;
}
