import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 1000000007;
    static int[][] dp;
    static int[] fac, inv;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());

        int n = Integer.parseInt(st.nextToken());
        int a = Integer.parseInt(st.nextToken());
        int b = Integer.parseInt(st.nextToken());
        int c = Integer.parseInt(st.nextToken());
        int d = Integer.parseInt(st.nextToken());

        fac = new int[n + 1];
        inv = new int[n + 1];
        fac[0] = 1;
        for (int i = 1; i <= n; i++) {
            fac[i] = (int) ((long) fac[i - 1] * i % MOD);
        }
        inv[n] = pow(fac[n], MOD - 2);
        for (int i = n - 1; i >= 0; i--) {
            inv[i] = (int) ((long) inv[i + 1] * (i + 1) % MOD);
        }

        dp = new int[n + 1][n + 1];
        for (int i = 0; i <= n; i++) {
            Arrays.fill(dp[i], -1);
        }

        pw.println(divide(n, a, b, c, d));
        pw.close();
    }

    static int divide(int n, int a, int b, int c, int d) {
        if (n < a || n > b) return 0;
        if (n == a && n == b) return 1;
        if (n == c || n == d) return 0;

        if (dp[n][n] != -1) return dp[n][n];

        int res = 0;
        for (int i = a; i <= Math.min(n, b); i++) {
            int x = divide(n - i, a, b, c, d);
            if (c <= x && x <= d) {
                res = (res + (int) ((long) fac[n] * inv[i] % MOD * inv[n - i] % MOD * x % MOD)) % MOD;
            }
        }
        dp[n][n] = res;
        return res;
    }

    static int pow(int x, int y) {
        int res = 1;
        while (y > 0) {
            if ((y & 1) == 1) res = (int) ((long) res * x % MOD);
            x = (int) ((long) x * x % MOD);
            y >>= 1;
        }
        return res;
    }
}
