    std::vector<int> dp(k + 1, 0);
    dp[0] = 1;

    for (int i = 1; i <= k; i++) {
        for (int j = 0; j <= k; j++) {
            if (i + j <= k) {
                dp[i + j] += dp[i];
            }
        }
    }

    int ans = 0;
    for (int i = 0; i <= k; i++) {
        for (int j = 0; j <= k; j++) {
            if (i + j <= k && i + j + (k - i - j) == s) {
                ans += dp[i] * dp[j] * dp[k - i - j];
            }
        }
    }

    std::cout << ans << std::endl;

    return 0;
}
