void dfs(int x, int y, int tx, int ty, int sx, int sy, int dir, int len) {
    if (x == tx && y == ty) {
        if (len < MAX) {
            path[len] = '\0';
            for (int i = 0; i < len; i++) {
                if (i % 2 == 0) {
                    if (path[i] == 'U') {
