                    } else if (path[i] == 'D') {
                        printf("Down");
                    } else if (path[i] == 'L') {
                        printf("Left");
                    } else if (path[i] == 'R') {
                        printf("Right");
                    }
                }
            }
            exit(0);
        }
        return;
    }
    if (x < tx) {
        path[len++] = 'R';
        dfs(x + 1, y, tx, ty, sx, sy, dir, len);
        path[len++] = 'L';
    }
    if (y < ty) {
        path[len++] = 'U';
        dfs(x, y + 1, tx, ty, sx, sy, dir, len);
        path[len++] = 'D';
    }
    if (dir == 0) {
        dfs(sx, sy, tx, ty, sx, sy, 1, 0);
    }
}

