#include <bits/stdc++.h>
using namespace std;

const int N = 105;
int n, m, dis[N][N], vis[N][N];
vector<pair<int, int>> e[N];

void dijkstra(int s) {
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            dis[s][j] = 1e9;
            vis[s][j] = 0;
        }
    }
    dis[s][s] = 0;
    priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>> q;
    q.push({0, s});
    while (!q.empty()) {
        int u = q.top().second;
        q.pop();
        if (vis[s][u]) continue;
        vis[s][u] = 1;
        for (auto [v, w] : e[u]) {
            if (dis[s][v] > dis[s][u] + w) {
                dis[s][v] = dis[s][u] + w;
                q.push({dis[s][v], v});
            }
        }
    }
}

int main() {
    scanf("%d%d", &n, &m);
    for (int i = 1; i <= m; i++) {
        int u, v, w;
        scanf("%d%d%d", &u, &v, &w);
        e[u].push_back({v, w});
        e[v].push_back({u, w});
    }
    int ans = 0;
    for (int i = 1; i <= n; i++) {
        dijkstra(i);
        for (int j = 1; j <= n; j++) {
            for (int k = 1; k <= n; k++) {
                if (dis[i][j] + dis[j][k] != dis[i][k]) {
                    ans++;
                    break;
                }
            }
        }
    }
    printf("%d\n", m - ans);
    return 0;
}

