        b = new int[M];
        c = new int[M];
        d = new int[N][N];
        vis = new boolean[N];
        g = new List[N];
        for (int i = 0; i < N; i++) {
            g[i] = new ArrayList<>();
        }
        for (int i = 0; i < M; i++) {
            st = new StringTokenizer(br.readLine());
            a[i] = Integer.parseInt(st.nextToken()) - 1;
            b[i] = Integer.parseInt(st.nextToken()) - 1;
            c[i] = Integer.parseInt(st.nextToken());
            g[a[i]].add(b[i]);
            g[b[i]].add(a[i]);
        }
        for (int i = 0; i < N; i++) {
            Arrays.fill(d[i], Integer.MAX_VALUE);
            d[i][i] = 0;
        }
        for (int i = 0; i < M; i++) {
            d[a[i]][b[i]] = c[i];
            d[b[i]][a[i]] = c[i];
        }
        for (int k = 0; k < N; k++) {
            for (int i = 0; i < N; i++) {
                for (int j = 0; j < N; j++) {
                    d[i][j] = Math.min(d[i][j], d[i][k] + d[k][j]);
                }
            }
        }
        int ans = 0;
        for (int i = 0; i < M; i++) {
            boolean flag = true;
            for (int j = 0; j < N; j++) {
                if (d[a[i]][j] + d[b[i]][j] == d[a[i]][b[i]]) {
                    flag = false;
                    break;
                }
            }
            if (flag) {
                ans++;
            }
        }
        System.out.println(M - ans);
    }
}

