    for (auto& [k, v] : cnt) {
        if (v > 1) {
            ans = ans * (v * (v - 1) / 2) % MOD;
        }
    }

    for (int i = 1; i <= n; i++) {
        if (cnt[i] == 1) {
            ans = ans * i % MOD;
        }
    }

    for (int i = 1; i <= n; i++) {
        ans = ans * factorial(n - 1, MOD) % MOD;
    }

    cout << ans << endl;

    return 0;
}

long long factorial(int n, int mod) {
    long long ans = 1;
    for (int i = 2; i <= n; i++) {
        ans = ans * i % mod;
    }
    return ans;
}
