```python
MOD = 10**9 + 7

def factorial(n):
    f = [1] * (n + 1)
    for i in range(1, n + 1):
        f[i] = f[i - 1] * i % MOD
    return f

def inverse(f, n):
    inv = [0] * (n + 1)
    inv[n] = pow(f[n], MOD - 2, MOD)
    for i in range(n - 1, -1, -1):
        inv[i] = inv[i + 1] * (i + 1) % MOD
    return inv

def solve():
    n = int(input())
    a = list(map(int, input().split()))
    f = factorial(n)
    inv = inverse(f, n)
    cnt = [0] * (n + 1)
    for i in range(1, n + 1):
        cnt[a[i - 1]] += 1
    ans = 1
    for i in range(1, n + 1):
        if cnt[i] == 0:
            ans = ans * f[n - 1] % MOD
        elif cnt[i] == 1:
            ans = ans * (f[n - 1] - f[cnt[i + 1] - 1]) % MOD
        else:
            ans = ans * (f[n - 1] - f[cnt[i + 1] - 1]) % MOD
            ans = ans * inv[cnt[i] - 1] % MOD
    print(ans)

if __name__ == "__main__":
    solve()
