public class Main {
    static final int MOD = 1000000007;
    static int n;
    static int[] a;
    static int[] cnt;
    static int[] vis;
    static int[] id;
    static int[] inv;
    static int[] fac;
    static int[] invfac;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());
        n = Integer.parseInt(st.nextToken());
        a = new int[n + 1];
        cnt = new int[n + 1];
        vis = new int[n + 1];
        id = new int[n + 1];
        inv = new int[n + 1];
        fac = new int[n + 1];
        invfac = new int[n + 1];
        st = new StringTokenizer(br.readLine());
        for (int i = 1; i <= n; i++) {
            a[i] = Integer.parseInt(st.nextToken());
            cnt[a[i]]++;
        }
        for (int i = 1; i <= n; i++) {
            if (cnt[i] == 0) {
                id[i] = i;
            }
        }
        for (int i = 1; i <= n; i++) {
            if (cnt[i] > 0) {
                int j = i;
                while (cnt[j] > 0) {
                    vis[j] = 1;
                    id[j] = i;
                    j = a[j];
                    cnt[j]--;
                }
            }
        }
        for (int i = 1; i <= n; i++) {
            if (vis[i] == 0) {
                int j = i;
                while (vis[j] == 0) {
                    vis[j] = 1;
                    j = a[j];
                }
                int k = j;
                while (vis[k] == 1) {
                    vis[k] = 0;
                    k = a[k];
                }
                id[i] = k;
            }
        }
        for (int i = 1; i <= n; i++) {
            inv[i] = qpow(i, MOD - 2);
        }
        fac[0] = 1;
        for (int i = 1; i <= n; i++) {
            fac[i] = (int) ((long) fac[i - 1] * i % MOD);
        }
        invfac[n] = qpow(fac[n], MOD - 2);
        for (int i = n - 1; i >= 0; i--) {
            invfac[i] = (int) ((long) invfac[i + 1] * (i + 1) % MOD);
        }
        long ans = 1;
        for (int i = 1; i <= n; i++) {
            if (id[i] == i) {
                ans = (ans * fac[cnt[i]]) % MOD;
            } else {
                ans = (ans * fac[cnt[id[i]]]) % MOD;
            }
        }
        for (int i = 1; i <= n; i++) {
            if (id[i] == i) {
                ans = (ans * invfac[cnt[i] - 1]) % MOD;
            } else {
                ans = (ans * invfac[cnt[id[i]] - 1]) % MOD;
            }
        }
        ans = (ans * fac[n]) % MOD;
        pw.println(ans);
        pw.close();
    }

    static long qpow(long a, long b) {
        long res = 1;
        while (b > 0) {
            if ((b & 1) == 1) {
                res = res * a % MOD;
            }
            a = a * a % MOD;
            b >>= 1;
        }
        return res;
    }
}
