#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define N 200005

int n, m, cnt, ans;
int head[N], to[N << 1], nxt[N << 1], tot;
int f[N], dep[N], son[N], siz[N], top[N], dfn[N], num;
int s[N], fa[N], st[N], ed[N], val[N];

void add(int u, int v) {
    to[++tot] = v;
    nxt[tot] = head[u];
    head[u] = tot;
}

void dfs1(int u, int p) {
    siz[u] = 1;
    fa[u] = p;
    dep[u] = dep[p] + 1;
    for (int i = head[u]; i; i = nxt[i]) {
        int v = to[i];
        if (v == p) continue;
        dfs1(v, u);
        siz[u] += siz[v];
        if (siz[v] > siz[son[u]]) son[u] = v;
    }
}

void dfs2(int u, int tp) {
    top[u] = tp;
    st[u] = ++num;
    dfn[u] = num;
    if (son[u]) dfs2(son[u], tp);
    for (int i = head[u]; i; i = nxt[i]) {
        int v = to[i];
        if (v == fa[u] || v == son[u]) continue;
        dfs2(v, v);
    }
    ed[u] = num;
}

int lca(int u, int v) {
    while (top[u] != top[v]) {
        if (dep[top[u]] < dep[top[v]]) std::swap(u, v);
        u = fa[top[u]];
    }
    return dep[u] < dep[v] ? u : v;
}

int query(int u, int v) {
    int l = lca(u, v);
    return dfn[u] <= dfn[l] && dfn[l] <= dfn[v] && ed[u] >= ed[l] && ed[l] >= ed[v];
}

int main() {
    scanf("%d", &n);
    for (int i = 1; i < n; i++) {
        int u, v;
        scanf("%d%d", &u, &v);
        add(u, v);
        add(v, u);
    }
    scanf("%s", s + 1);
    for (int i = 1; i <= n; i++) {
        if (s[i] == '1') val[i] = 1;
    }
    dfs1(1, 0);
    dfs2(1, 1);
    for (int i = 1; i <= n; i++) {
        if (val[i]) {
            for (int j = 1; j <= n; j++) {
                if (val[j]) {
                    ans += query(i, j);
                }
            }
        }
    }
    printf("%d\n", ans - n);
    return 0;
}
