using namespace std;

const int N = 2e5 + 5;

int n, m, cnt, ans, dep[N], mx[N], fa[N], son[N], top[N], siz[N];
vector<int> e[N], g[N];
queue<int> q;

void dfs(int u, int f) {
    fa[u] = f;
    siz[u] = 1;
    for (int v : e[u]) {
        if (v == f) continue;
        dep[v] = dep[u] + 1;
        dfs(v, u);
        siz[u] += siz[v];
        if (siz[v] > siz[son[u]]) son[u] = v;
    }
}

void dfs2(int u, int t) {
    top[u] = t;
    mx[u] = dep[u];
    if (son[u]) {
        dfs2(son[u], t);
    }
    for (int v : e[u]) {
        if (v != fa[u] && v != son[u]) {
            dfs2(v, v);
        }
    }
}

int lca(int u, int v) {
    while (top[u] != top[v]) {
        if (dep[top[u]] < dep[top[v]]) swap(u, v);
        u = fa[top[u]];
    }
    return dep[u] < dep[v] ? u : v;
}

int main() {
    cin >> n;
    for (int i = 1; i < n; i++) {
        int u, v;
        cin >> u >> v;
        e[u].push_back(v);
        e[v].push_back(u);
    }
    string s;
    cin >> s;
    for (int i = 1; i <= n; i++) {
        if (s[i - 1] == '1') {
            q.push(i);
        }
    }
    dfs(1, 0);
    dfs2(1, 1);
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (int v : e[u]) {
            if (v != fa[u]) {
                mx[v] = max(mx[v], mx[u]);
                if (mx[v] == dep[v]) {
                    q.push(v);
                }
            }
        }
    }
    for (int i = 1; i <= n; i++) {
        if (s[i - 1] == '1') {
            int u = i;
            int d = dep[u];
            for (int v : g[u]) {
                d = min(d, dep[v]);
            }
            ans += (d + 1) * (n - d);
        }
    }
    cout << ans << endl;
    return 0;
