from collections import deque
import sys

input = sys.stdin.readline

N = int(input())
G = [[] for _ in range(N)]
for _ in range(N - 1):
    u, v = map(int, input().split())
    G[u - 1].append(v - 1)
    G[v - 1].append(u - 1)

S = [int(c) for c in input().rstrip()]

def dfs(u, p, d, S):
    if S[u] == 0:
        return 1
    res = 1
    for v in G[u]:
        if v != p:
            res *= dfs(v, u, d, S)
    return res

def bfs(u, p, d, S):
    q = deque([(u, p, d, S)])
    seen = [False] * N
    seen[u] = True
    res = 1
    while q:
        u, p, d, S = q.popleft()
        if S[u] == 0:
            return 1
        for v in G[u]:
            if v != p and not seen[v]:
                seen[v] = True
                if d > 0:
                    q.append((v, u, d - 1, S))
                else:
                    q.append((v, u, d, S))
        res += 1
    return res

ans = 1
for i in range(N):
    if S[i] == 1:
        ans *= bfs(i, -1, 0, S)

print(ans - 1)
