        for (int i = 0; i < N; i++) {
            g[i] = new ArrayList<>();
        }

        for (int i = 0; i < N - 1; i++) {
            st = new StringTokenizer(br.readLine());
            a[i] = Integer.parseInt(st.nextToken()) - 1;
            b[i] = Integer.parseInt(st.nextToken()) - 1;
            g[a[i]].add(b[i]);
            g[b[i]].add(a[i]);
        }

        st = new StringTokenizer(br.readLine());
        for (int i = 0; i < N; i++) {
            s[i] = Integer.parseInt(st.nextToken()) - 48;
        }

        dfs(0, -1);

        int max = 0;
        for (int i = 0; i < N; i++) {
            if (s[i] == 1) {
                max = Math.max(max, depth[i]);
            }
        }

        for (int i = 0; i < N; i++) {
            if (s[i] == 1) {
                dfs(i, -1);
                int cnt = 0;
                for (int j = 0; j < N; j++) {
                    if (s[j] == 1 && depth[j] <= max) {
                        cnt++;
                    }
                }
                ans += cnt;
                dfs(i, -1);
            }
        }

        pw.println(ans);
        pw.close();
    }

    static void dfs(int v, int p) {
        for (int u : g[v]) {
            if (u != p) {
                depth[u] = depth[v] + 1;
                dfs(u, v);
            }
        }
    }
