    vector<int> minPrefix(n + 1, 0);
    vector<int> maxSuffix(n + 1, 0);
    for (int i = 1; i <= n; ++i) {
        if (s[i - 1] == '-') {
            minPrefix[i] = minPrefix[i - 1] + 1;
        } else {
            minPrefix[i] = minPrefix[i - 1];
        }
    }
    for (int i = n - 1; i >= 0; --i) {
        if (s[i] == '-') {
            maxSuffix[i] = maxSuffix[i + 1] - 1;
        } else {
            maxSuffix[i] = maxSuffix[i + 1];
        }
    }
    long long res = 0;
    for (int i = 1; i < n; ++i) {
        int j = minPrefix[i];
        int k = maxSuffix[i + 1];
        if (j <= k) {
            string t = s.substr(0, i) + "(" + s.substr(i, 1) + ")" + s.substr(i + 1);
            res = max(res, eval(t));
        }
    }
    return res;
}

int main() {
    int n;
    cin >> n;
    string s;
    cin >> s;
    cout << maxVal(s) << endl;
    return 0;
}
