    for (int i = 1; i <= n; i++) {
        pre2[i] = pre2[i - 1] + t[i] * i;
    }

    vector<int> pre3(n + 1);
    for (int i = 1; i <= n; i++) {
        pre3[i] = pre3[i - 1] + t[i] * i * (i - 1) / 2;
    }

