import java.io.*;
import java.util.*;

public class Main {
    static class SegmentTree {
        int n;
        long[] tree;

        public SegmentTree(int n) {
            this.n = n;
            tree = new long[4 * n];
            build(1, 1, n);
        }

        void build(int v, int tl, int tr) {
            if (tl == tr) {
                tree[v] = 0;
            } else {
                int tm = (tl + tr) / 2;
                build(v * 2, tl, tm);
                build(v * 2 + 1, tm + 1, tr);
                tree[v] = tree[v * 2] + tree[v * 2 + 1];
            }
        }

        void update(int v, int tl, int tr, int pos, long val) {
            if (tl == tr) {
                tree[v] = val;
            } else {
                int tm = (tl + tr) / 2;
                if (pos <= tm) {
                    update(v * 2, tl, tm, pos, val);
                } else {
                    update(v * 2 + 1, tm + 1, tr, pos, val);
                }
                tree[v] = tree[v * 2] + tree[v * 2 + 1];
            }
        }

        long query(int v, int tl, int tr, int l, int r) {
            if (l > r) {
                return 0;
            }
            if (tl == l && tr == r) {
                return tree[v];
            }
            int tm = (tl + tr) / 2;
            return query(v * 2, tl, tm, l, Math.min(r, tm)) +
                   query(v * 2 + 1, tm + 1, tr, Math.max(l, tm + 1), r);
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());
        int n = Integer.parseInt(st.nextToken());
        int[] t = new int[n + 1];
        st = new StringTokenizer(br.readLine());
        for (int i = 1; i <= n; i++) {
            t[i] = Integer.parseInt(st.nextToken());
        }
        int m = Integer.parseInt(br.readLine());
        int[][] p = new int[m][2];
        for (int i = 0; i < m; i++) {
            st = new StringTokenizer(br.readLine());
            p[i][0] = Integer.parseInt(st.nextToken());
            p[i][1] = Integer.parseInt(st.nextToken());
        }
        Arrays.sort(p, Comparator.comparingInt(a -> a[1]));
        long[] ans = new long[m];
        SegmentTree stree = new SegmentTree(n);
        for (int i = 0; i < m; i++) {
            int[] cur = p[i];
            int pos = cur[0];
            long x = cur[1];
            long[] pref = new long[n + 1];
            for (int j = 1; j <= n; j++) {
                pref[j] = pref[j - 1] + t[j];
            }
            long[] suff = new long[n + 1];
            for (int j = n; j >= 1; j--) {
                suff[j] = suff[j + 1] + t[j];
            }
            for (int j = 1; j <= n; j++) {
                stree.update(1, 1, n, j, pref[j] + suff[j] - t[j] * j);
            }
            long res = 0;
            for (int j = 1; j <= n; j++) {
                long val = stree.query(1, 1, n, j, n);
                res = Math.max(res, val - (pref[j] + suff[j] - t[j] * j));
            }
            ans[i] = res;
            for (int j = 1; j <= n; j++) {
                stree.update(1, 1, n, j, (j - 1) * t[j] - (pref[j] + suff[j] - t[j] * j));
            }
        }
        for (long i : ans) {
            pw.println(i);
        }
        pw.close();
    }
}
