#include <iostream>
#include <string>
#include <unordered_map>

using namespace std;

const string s1 = "dream";
const string s2 = "dreamer";
const string s3 = "erase";
const string s4 = "eraser";

unordered_map<string, int> dp;

bool dfs(const string& s, int i) {
    if (i == s.size()) return true;
    if (dp.find(s.substr(i)) != dp.end()) return dp[s.substr(i)] == 1;
    bool res = false;
    if (i + 5 <= s.size() && s.substr(i, 5) == s1) res |= dfs(s, i + 5);
    if (i + 7 <= s.size() && s.substr(i, 7) == s2) res |= dfs(s, i + 7);
    if (i + 5 <= s.size() && s.substr(i, 5) == s3) res |= dfs(s, i + 5);
    if (i + 6 <= s.size() && s.substr(i, 6) == s4) res |= dfs(s, i + 6);
    dp[s.substr(i)] = res ? 1 : 0;
    return res;
}

int main() {
    string s;
    cin >> s;
    cout << (dfs(s, 0) ? "YES" : "NO") << endl;
    return 0;
}
