            st = new StringTokenizer(br.readLine());
            p[i] = Integer.parseInt(st.nextToken()) - 1;
            q[i] = Integer.parseInt(st.nextToken()) - 1;
        }

        for (int i = 0; i < L; i++) {
            st = new StringTokenizer(br.readLine());
            r[i] = Integer.parseInt(st.nextToken()) - 1;
            s[i] = Integer.parseInt(st.nextToken()) - 1;
        }

        for (int i = 0; i < N; i++) {
            parent[i] = i;
            roadSize[i] = 1;
            railSize[i] = 1;
        }

        for (int i = 0; i < K; i++) {
            union(p[i], q[i], roadSize);
        }

        for (int i = 0; i < L; i++) {
            union(r[i], s[i], railSize);
        }

        for (int i = 0; i < N; i++) {
            ans[find(i, roadSize)] += roadSize[i];
            ans[find(i, railSize)] += railSize[i];
        }

        for (int i = 0; i < N; i++) {
