    static int find(int x, int[] size) {
        if (parent[x] != x) {
            parent[x] = find(parent[x], size);
        }
        return parent[x];
    }

    static void union(int x, int y, int[] size) {
        int rootX = find(x, size);
        int rootY = find(y, size);
        if (rootX != rootY) {
            if (size[rootX] > size[rootY]) {
                parent[rootY] = rootX;
                size[rootX] += size[rootY];
            } else {
                parent[rootX] = rootY;
                size[rootY] += size[rootX];
            }
        }
    }
