class UnionFind:
    def __init__(self, n):
        self.parent = list(range(n))
        self.size = [1] * n

    def find(self, a):
        acopy = a
        while a != self.parent[a]:
            a = self.parent[a]
        while acopy != a:
            self.parent[acopy], acopy = a, self.parent[acopy]
        return a

    def union(self, a, b):
        a, b = self.find(a), self.find(b)
        if a == b:
            return
        if self.size[a] < self.size[b]:
            a, b = b, a
        self.parent[b] = a
        self.size[a] += self.size[b]

uf = UnionFind(N + 1)
for i in range(1, N + 1):
    if i != a and i != b:
        d = abs(x[i] - x[a]) + abs(y[i] - y[a])
        uf.union(i, a)
        for j in range(1, N + 1):
            if j != a and j != b and j != i:
                d2 = abs(x[j] - x[i]) + abs(y[j] - y[i])
                if d2 == d:
                    uf.union(j, i)

ans = 0
for i in range(1, N + 1):
    if i != a and i != b:
        ans += uf.size[uf.find(i)] - 1

