    scanf("%d%d%s", &n, &m, s);
    for (int i = 1; i <= m; i++) {
        scanf("%d%d", &l[i], &r[i]);
    }
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= n; j++) {
            for (int k = 0; k <= 1; k++) {
                dp[i][j][k] = 0;
            }
        }
    }
    dp[0][0][0] = 1;
    for (int i = 1; i <= m; i++) {
        for (int j = 0; j <= n; j++) {
            for (int k = 0; k <= 1; k++) {
                if (j < l[i]) {
                    dp[i][j][k] = dp[i - 1][j][k];
                } else if (j < r[i]) {
                    dp[i][j][k] = (dp[i - 1][j][k] + dp[i - 1][j - 1][k]) % MOD;
                } else {
                    dp[i][j][k] = (dp[i - 1][j][k] + dp[i - 1][j - 1][k] + dp[i - 1][j - 1][1 - k]) % MOD;
                }
            }
        }
    }
    int ans = 0;
    for (int i = 0; i <= n; i++) {
        ans = (ans + dp[m][i][0]) % MOD;
    }
    printf("%d\n", ans);
    return 0;
}
