        dp[i][i] = 1;
    }

    for (int i = 1; i < n; i++) {
        for (int j = 0; j < n - i; j++) {
            int k = j + i;
            dp[j][k] = (dp[j][k - 1] + dp[j + 1][k]) % MOD;
        }
    }

    vector<vector<int>> dp2(n + 1, vector<int>(n + 1));
    for (int i = 0; i <= n; i++) {
        dp2[i][i] = 1;
    }

    for (int i = 1; i < n; i++) {
        for (int j = 0; j < n - i; j++) {
            int k = j + i;
            if (s[j] == s[k]) {
                dp2[j][k] = (dp2[j + 1][k] + dp2[j][k - 1]) % MOD;
            } else {
                dp2[j][k] = (dp2[j + 1][k] + dp2[j][k - 1] + dp[j + 1][k - 1]) % MOD;
            }
        }
    }

    int ans = 0;
    for (int i = 0; i < n; i++) {
        if (s[i] == '0') {
            ans = (ans + dp2[0][i]) % MOD;
        }
    }

    for (int i = 0; i < m; i++) {
        int len = r[i] - l[i] + 1;
        for (int j = 0; j < len; j++) {
            dp2[l[i]][l[i] + j] = (dp2[l[i]][l[i] + j] + dp2[l[i]][l[i] + j - 1]) % MOD;
        }
        for (int j = len - 1; j >= 0; j--) {
            dp2[r[i] - j + 1][r[i]] = (dp2[r[i] - j + 1][r[i]] + dp2[r[i] - j + 1][r[i] - 1]) % MOD;
        }
        for (int j = 0; j < n; j++) {
            if (s[j] == '0') {
                ans = (ans + dp2[0][j]) % MOD;
            }
        }
    }

